import { Component, OnInit } from '@angular/core';
import { Review } from 'src/app/entities/review';
import { AppService } from 'src/app/services/appservice.service';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'add-review',
  templateUrl: './add-review.component.html',
  styleUrls: ['./add-review.component.scss']
})
export class AddReviewComponent implements OnInit  {

  addReivewForm: FormGroup | undefined;

  constructor(private appService:AppService)
  {

  }
  ngOnInit(): void {
    addReivewForm = new FormGroup({
      name: new FormControl(null, Validators.required),
      reviewDescription: new FormControl(null, Validators.required),
      category: new FormControl(null, Validators.required)
    });
  }

  onSubmit()
  {    
    alert('Hi I am here');
    debugger;
  }

}
<h1>Add a Review</h1>

<form (ngSubmit)="onSubmit()" [formGroup]="addReivewForm" class="form">
  <div class="form-group">
    <label for="name">Name</label>
    <input
      type="text"
      class="form-control br-0"
      id="name"
      required
      [formControl]="name"
      name="name"
    />
  </div>
  <div class="form-group">
    <label for="reviewDescription">Review Description</label>
    <input
      type="text"
      class="form-control br-0"
      id="reviewDescription"
      required
      [formControl]="reviewDescription"
      name="reviewDescription"
    />
  </div>
  <div class="form-group">
    <label for="category">Category</label>
    <input
      type="text"
      class="form-control br-0"
      id="category"
      required
      [formControl]="category"
      name="category"
    />
  </div>
  <button type="submit" class="btn btn-primary mt-3 br-0">Edit</button>
</form>
